// NaviIcon.tsx
import React from "react";
import styled from "styled-components";

export interface NaviIconProps {
  mode: "Default" | "Clicked" | "Unclicked";
  label: string;
  icon: string;
  onClick: () => void;
}

const NaviIconWrapper = styled.div<{ mode: string }>`
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 12px;
  border-radius: 12px;
  cursor: pointer;
  background-color: ${({ mode, theme }) =>
    mode === "Clicked" ? theme.colors.purple[100] : "transparent"};
  &:hover {
    background-color: ${({ theme }) => theme.colors.gray[200]};
  }
`;

const Icon = styled.img`
  width: 48px;
  height: 48px;
  margin-bottom: 8px;
`;

const Label = styled.span`
  font-size: ${({ theme }) => theme.typography.T7.size};
  color: ${({ theme }) => theme.colors.black};
`;

const NaviIcon: React.FC<NaviIconProps> = ({ mode, label, icon, onClick }) => {
  return (
    <NaviIconWrapper mode={mode} onClick={onClick}>
      <Icon src={icon} alt={label} />
      <Label>{label}</Label>
    </NaviIconWrapper>
  );
};

export default NaviIcon;
