import React, { useState } from "react";
import styled from "styled-components";

const ToggleWrapper = styled.div<{ isOn: boolean }>`
  display: flex;
  align-items: center;
  justify-content: ${({ isOn }) => (isOn ? "flex-end" : "flex-start")};
  width: 80px;
  height: 40px;
  padding: 8px;
  background-color: ${({ theme }) => theme.colors.gray[200]};
  border-radius: 40px;
  box-shadow: 0px 1px 1px 1px rgba(0, 0, 0, 0.25) inset;
  cursor: pointer;
  position: relative;
`;

const ToggleCircle = styled.div<{ isOn: boolean }>`
  width: 64px;
  height: 24px;
  background-color: ${({ isOn, theme }) =>
    isOn ? theme.colors.purple[400] : theme.colors.gray[200]};
  border-radius: 30px;
  display: flex;
  align-items: center;
  justify-content: center;
  box-shadow: 0px 1px 1px 1px rgba(0, 0, 0, 0.25);
`;

const ToggleText = styled.span<{ isOn: boolean }>`
  color: ${({ theme }) => theme.colors.white};
  font-size: ${({ theme }) => theme.typography.T5.size};
  font-weight: ${({ theme }) => theme.typography.T5.weight};
  line-height: ${({ theme }) => theme.typography.T5.lineHeight};
`;

const Toggle = () => {
  const [isOn, setIsOn] = useState(false);

  const toggle = () => {
    setIsOn((prev) => !prev);
  };

  return (
    <ToggleWrapper isOn={isOn} onClick={toggle}>
      <ToggleCircle isOn={isOn}>
        <ToggleText isOn={isOn}>{isOn ? "ON" : "OFF"}</ToggleText>
      </ToggleCircle>
    </ToggleWrapper>
  );
};

export default Toggle;
