import React, { useState } from "react";
import styled from "styled-components";
import NaviIcon from "./NaviIcon";

const NavigationWrapper = styled.div`
  display: flex;
  justify-content: space-between;
  padding: 16px;
  background-color: ${({ theme }) => theme.colors.purple[100]};
  border-radius: 12px;
`;

const LabelWrapper = styled.div`
  display: flex;
  justify-content: space-between;
  margin-top: 8px; /* 아이콘과 라벨 사이 간격 */
  padding: 0 16px;
`;

const Label = styled.span<{ isSelected: boolean }>`
  font-size: ${({ theme }) => theme.typography.T6.size};
  font-weight: ${({ theme }) => theme.typography.T6.weight};
  color: ${({ isSelected, theme }) =>
    isSelected ? theme.colors.primary : theme.colors.black};
  text-align: center;
`;

const iconList = [
  { label: "모빌리티", icon: "/assets/icons/mobility.png" },
  { label: "냉장고", icon: "/assets/icons/refrigerator.png" },
  { label: "전자제품", icon: "/assets/icons/electronics.png" },
  { label: "책/문서", icon: "/assets/icons/books.png" },
  { label: "기프티콘", icon: "/assets/icons/gifticon.png" },
  { label: "원룸/오피스텔", icon: "/assets/icons/office.png" },
  { label: "족보", icon: "/assets/icons/secret.png" },
  { label: "기타", icon: "/assets/icons/others.png" },
];

const Navigation: React.FC = () => {
  const [selected, setSelected] = useState<number | null>(null);

  return (
    <>
      <NavigationWrapper>
        {iconList.map((item, index) => (
          <NaviIcon
            key={index}
            mode={
              selected === null
                ? "Default"
                : selected === index
                ? "Clicked"
                : "Unclicked"
            }
            icon={item.icon}
            onClick={() => setSelected(index)}
          />
        ))}
      </NavigationWrapper>
      <LabelWrapper>
        {iconList.map((item, index) => (
          <Label key={index} isSelected={selected === index}>
            {item.label}
          </Label>
        ))}
      </LabelWrapper>
    </>
  );
};

export default Navigation;
