import styled from "styled-components";
import { T1, T3, T7 } from "../../styles/typography";

export const HeaderContainer = styled.header`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 20px;
`;

export const LogoAndNav = styled.div`
  display: flex;
  align-items: center;

  & > nav {
    margin-left: 20px; /* 로고와 네비게이션 간 간격 */
  }
`;

export const Logo = styled.div`
  ${T1} /* 로고는 T1 스타일 적용 */
  color: ${(props) => props.theme.primary}; /* Primary 색상 */
`;

export const Nav = styled.nav`
  display: flex;
  align-items: center;
`;

export const NavItem = styled.div`
  ${T3} /* 네비게이션은 T3 스타일 적용 */
  margin-right: 20px;
`;

export const UserSection = styled.div`
  display: flex;
  align-items: center;
  ${T7} /* 사용자 섹션은 T7 스타일 적용 */
  margin-right: 20px;

  & > div {
    margin-right: 10px;
  }
`;

export const AuthSection = styled.div`
  display: flex;
  align-items: center;
`;

export const AuthButton = styled.button`
  display: flex;
  align-items: center;
  ${T7} /* 버튼도 T7 스타일 적용 */
  border: 1px solid black;
  padding: 8px 16px;
  background: white;
  cursor: pointer;
  margin-left: 10px;

  span {
    margin-right: 4px;
    color: ${(props) => props.theme.primary}; /* Primary 색상 */
  }
`;
