import styled from "styled-components";
import { T1, T3, T6, T7 } from "../../styles/typography";

export const HeaderContainer = styled.header`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 16px 32px;
  background-color: #fff;
  border-bottom: 1px solid #ddd;
`;

export const LogoAndNav = styled.div`
  display: flex;
  align-items: center;
`;

export const Logo = styled.div`
  ${T1}
  color: ${(props) => props.theme.primary};
`;

export const Nav = styled.nav`
  display: flex;
  align-items: center;
  margin-left: 24px; /* 로고와 네비게이션 간 간격 */
`;

export const NavItem = styled.div`
  ${T6}
  margin-right: 16px;

  &:last-child {
    margin-right: 0;
  }
`;

export const UserSection = styled.div`
  display: flex;
  align-items: center;
  ${T7}
  margin-right: 20px;

  & > div {
    margin-right: 8px;
  }
`;

export const AuthSection = styled.div`
  display: flex;
  align-items: center;
`;

export const AuthButton = styled.button`
  display: flex;
  align-items: center;
  justify-content: center;
  ${T7}
  border: 1px solid #111;
  padding: 8px 12px;
  background: white;
  cursor: pointer;
  margin-left: 8px;
  border-radius: 8px;

  span {
    margin-right: 4px;
    color: ${(props) => props.theme.naver};
  }
`;
