import React from "react";
import styled from "styled-components";
import {
    HeaderContainer,
    Logo,
    Nav,
    NavItem,
    UserSection,
    Username,
    LogoutButton,
    AuthSection,
    AuthButton,
    NLogo,
  } from "./Header.styles";

interface HeaderProps {
  isLoggedIn: boolean;
  username?: string; // 로그인 상태에서 사용자 이름 표시
}

const Header: React.FC<HeaderProps> = ({ isLoggedIn, username }) => {
  return (
    <HeaderContainer>
      <Logo>HUN:ter</Logo>
      <Nav>
        <NavItem>판매하기</NavItem>
        <NavItem>나의 거래</NavItem>
        <NavItem>채팅</NavItem>
      </Nav>
      {isLoggedIn ? (
        <UserSection>
          <Username>{username} 님</Username>
          <LogoutButton>Log Out</LogoutButton>
        </UserSection>
      ) : (
        <AuthSection>
          <AuthButton>
            <NLogo>N</NLogo> Sign Up
          </AuthButton>
          <AuthButton>
            <NLogo>N</NLogo> Login
          </AuthButton>
        </AuthSection>
      )}
    </HeaderContainer>
  );
};

export default Header;
