// RadioButton Wrapper
export const RadioButtonWrapper = styled.div`
  display: inline-flex;
  align-items: center; /* 수직 정렬 */
  gap: 16px;
  cursor: pointer;
  line-height: 1; /* 부모 컨테이너의 라인 높이 강제 설정 */
`;

// Radio Circle
export const RadioCircle = styled.div<{ checked: boolean }>`
  display: flex;
  justify-content: center;
  align-items: center;
  width: 24px;
  height: 24px;
  border-radius: 50%;
  border: 2px solid ${({ theme }) => theme.colors.purple[400]};
  position: relative; /* 내부 원을 정확히 정렬하기 위한 상대 위치 */

  &::after {
    content: '';
    display: ${({ checked }) => (checked ? 'block' : 'none')};
    width: 12px; /* 내부 원 크기 */
    height: 12px;
    border-radius: 50%;
    background-color: ${({ theme }) => theme.colors.primary};
  }
`;

// Radio Label
export const RadioLabel = styled.span`
  font-size: ${({ theme }) => theme.typography.T6.size};
  font-weight: ${({ theme }) => theme.typography.T6.weight};
  color: ${({ theme }) => theme.colors.black};
  line-height: 1; /* 텍스트의 라인 높이를 1로 고정 */
  display: flex;
  align-items: center; /* 텍스트의 수직 정렬 */
`;
